import React from 'react'

import ReactMarkdown from "react-markdown";

type Props = {
    content:string
}

export default function Heading(props:Props) {
    const headings:string[]|null = props.content.match(/<h3 id=".+">.+<\/h3>/g);
    console.log(headings)
    return (
        <div className="headings">
            {headings && headings.map((e:any) => {
                console.log(e)
                return (
                    <div className="heading" dangerouslySetInnerHTML={e} />
                )
            })}
        </div>
    )
}
