type Props = {
	content: string;
};

export default function Heading(props: Props) {
	const headings: string[] | null = props.content.match(
		/<h3 id=".+">.+<\/h3>/g
	);
	console.log(headings);
	return (
		<div className="headings">
			{headings &&
				headings.map((e: string) => {
                    let element:string = e.replace(/h3/g,"a")
                    element = e.replace(/id/,"href")
					return (
						<div className="heading">{ReactHtmlParser(element)}</div>
					);
				})}
		</div>
	);
}
