const path = require('path')

const rootPath = path.resolve('')
const assetsPath = path.join(rootPath, 'assets')
const publicPath = path.join(rootPath, 'public')

module.exports = {
    root: rootPath,
    assets: assetsPath,
    public: publicPath,
}
